step.script:
Integer v = (Integer) step.getVariable("VAR1");
if (v == null){
    v = 1;
}else{
    v = v + 1;
}        
try {
    Thread.sleep(50);
} catch (InterruptedException ex) {
    System.out.println("Step was interrupted while sleeping 1");
}
step.setVariable("VAR1", v);
try {
    Thread.sleep(50);
} catch (InterruptedException ex) {
    System.out.println("Step was interrupted while sleeping 2");
}
step.setValue(v);
